import Layout from "../components/layout/layout";
import { Form, Button } from "react-bootstrap";
import "./index.scss";
const Index = () => {
  return (
    <Layout bgColor="#9ad3bc">
      <div className="login container p-5">
        <h3 className="text-center p-2">Đăng nhập</h3>
        <Form className="login-form mw-75">
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control className="form-input" type="email" placeholder="Tên đăng nhập" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control className="form-input" type="password" placeholder="Mật khẩu" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>{" "}
      </div>
    </Layout>
  );
};
export default Index;
